totalSupply = contract.functions.totalSupply().call()
contract_name=contract.functions.name().call()
contract_symbol=contract.functions.symbol().call()
balance=contract.functions.balanceOf('0xDFd5293D8e347dFe59E90eFd55b2956a1343963d').call()
#balance
print("额度:",web3_sum(balance))
#小数点18位
print("总数:",web3.from_wei(totalSupply,'ether'))
